import React, { useState } from 'react';
import "../../styles/style.scss";

const ContestContext  = ({ title, buttons, dataList }) => {
    const [selectedAnswer, setSelectedAnswer] = useState(null);
    const renderButtons = () => {
        return buttons.map((button, index) => (
            <button
                key={index}
                style={{ backgroundColor: button.color }}
                onClick={button.onClick}
            >
                {button.text}
            </button>
        ));
    };

    const handleAnswerClick = (answerId) => {
        setSelectedAnswer(answerId);
    };

    const renderDataList = () => {
        // 从 dataList 中排除第一个元素，因为它是标题
        const answers = dataList.slice(0);

        return (
            <div className="result">
                <div className="result_list">
                    {answers.map((data, index) => (
                        <div key={index} className='result_answers'
                            onClick={() => handleAnswerClick(data.id)}>
                            <p>{data.name}</p>
                        </div>
                    ))}
                </div>
            </div>
        );
    };

    return (
        <div className='point_frame'>
            <div className='point_view'>
                {/* <p className='group_view-title font24'>{title}</p> */}

                {renderDataList()}

            </div>
            <div className='group_btn'>
                {renderButtons()}
            </div>
        </div>
    );
};

export default ContestContext ;
