import React from 'react';
import "../../styles/style.scss";

const GroupContext2 = ({ title, buttons, dataList, activeComponent, onButtonClick }) => {
    const renderButtons = () => {
        return buttons.map((button) => (
            <button
                key={button.id}
                style={{ backgroundColor: button.color, marginRight: button.id === activeComponent ? '20px' : '40px' }}
                onClick={() => {
                    onButtonClick(button.id);
                    button.onClick();
                }}
                className={button.id === activeComponent ? 'active' : ''}
            >
                {button.text}
            </button>
        ));
    };

    const renderDataList = () => {
        return (
            <div className="result">
                <div className="result_list">
                    {dataList.map((data, index) => (
                        <div key={index} className='result_answers'>
                            <p>{data.names.join(', ')}</p>
                        </div>
                    ))}
                </div>
            </div>
        );
    };

    return (
        <div className='point_frame'>
            <div className='point_view'>
                <p className='group_view-title font24'>{title}</p>
                {renderDataList()}
            </div>
            <div className='group_btn'>
                {renderButtons()}
            </div>
        </div>
    );
};

export default GroupContext2;
