import React from 'react';
import "../styles/style.scss";
import GroupPublic from './GroupPublic';
import { useHistory } from 'react-router-dom';


const GroupContext = ({ title, buttons }) => {
    const history = useHistory();

    const handleButtonClick = (button) => {
        console.log(`Button "${button.text}" clicked!`);

        // 根据按钮的不同行为执行不同的逻辑
        if (button.id === 'extendTime') {
            // 执行延长时间的逻辑
            console.log('Extending time...');
        } else if (button.id === 'restart') {
            // 执行重新开始的逻辑
            console.log('Restarting...');
        } else if (button.id === 'endGrouping') {
            // 执行结束分组的逻辑
            console.log('Ending grouping...');

            // 在这里执行导航到 GroupFinish 页面的逻辑
            history.push(button.path);
        }

        // 在这里执行其他逻辑
        };

    const renderButtons = () => {
        return buttons.map((button, index) => (
            <button 
            key={index} 
            style={{ backgroundColor: button.color }}
            onClick={() => handleButtonClick(button)}
            >
            {button.text}
            </button>
        ));
        };

    const handleButtonClick2 = () => {
        console.log('Button clicked!');
    };

    return (
        <div className='group_frame'>
            <div className='group_view'>
                <p className='group_view-title font24'>{title}</p>
                <section className='group_view-context'>
                    <GroupPublic onClick={handleButtonClick2} showButton={false} />
                    <GroupPublic onClick={handleButtonClick2} showButton={false} />
                    <GroupPublic onClick={handleButtonClick2} showButton={false} />
                    <GroupPublic onClick={handleButtonClick2} showButton={false} />
                </section>

            </div>
            <div className='group_btn'>
                {renderButtons()}
            </div>

        </div>
    );
};

export default GroupContext;
