import React from 'react';
import "../styles/style.scss";
import GroupPublic from './GroupPublic';


const GroupContext = ({ title, buttons }) => {

    const renderButtons = () => {
        return buttons.map((button, index) => (
            <button 
            key={index} 
            style={{ backgroundColor: button.color }}
            onClick={button.onClick}
            // onClick={() => handleButtonClick(button)}
            >
            {button.text}
            </button>
        ));
    };

    const handleButtonClick2 = () => {
        console.log('Button clicked!');
    };

    return (
        <div className='group_frame'>
            <div className='group_view'>
                <p className='group_view-title font24'>{title}</p>
                <section className='group_view-context'>
                    <GroupPublic onClick={handleButtonClick2} showButton={false} />
                    <GroupPublic onClick={handleButtonClick2} showButton={false} />
                    <GroupPublic onClick={handleButtonClick2} showButton={false} />
                    <GroupPublic onClick={handleButtonClick2} showButton={false} />
                </section>

            </div>
            <div className='group_btn'>
                {renderButtons()}
            </div>

        </div>
    );
};

export default GroupContext;
