import React, { useState } from 'react';

const GroupPublic = ({ memberId, memberNames, onClick, showButton }) => {
    const [isActive, setIsActive] = useState(false);

    const handleAddClick = () => {
        // 在這裡執行新增數據的邏輯
        // 這裡只是一個簡單的例子
        console.log('Adding new data...');

        // 調用父組件的 onClick 函數
        if (onClick) {
            onClick();
        }

        // 設定狀態以觸發更新，添加或移除 'active' class
        setIsActive(!isActive);
    };

    return (
        <div className='group_public-frame'>
            <div className={`group_public ${isActive ? 'active' : ''}`}>
                <div className='group_public-title'>
                    <p className='font16_medium'>{`組別 ${memberId}`}</p>
                </div>
                <section className='group_number'>
                    {memberNames.map((name, index) => (
                            <p key={index}>{name}</p>
                        ))}
                </section>
            </div>
            {showButton && (
                <button className='group_add' onClick={handleAddClick}></button>
            )}
        </div>
    );
};

export default GroupPublic;
