import React, { useState, useEffect } from 'react';
import "../styles/style.scss";
import Sidebar from './Sidebar';
import Back from '../assets/ic_back.svg'
import { Link } from 'react-router-dom';
import GroupContext from "./GroupContext";



const Group2 = () => {
    const initialTime = 120; // 初始时间为120秒
    const [remainingTime, setRemainingTime] = useState(initialTime);

    // 使用 useEffect 处理计时逻辑
    useEffect(() => {
        const timer = setInterval(() => {
            // 每秒减少一秒
            setRemainingTime(prevTime => prevTime - 1);
        }, 1000);

        // 在组件卸载时清除计时器
        return () => clearInterval(timer);
    }, []);

    const handleExtendTime = () => {
        // 点击“延长时间”按钮时，增加30秒
        setRemainingTime(prevTime => prevTime + 30);
    };
    const buttonsConfig = [
        { id: 'extendTime', text: '延長時間', color: '#474FB2', onClick: handleExtendTime},
        { text: '重新開始', color: '#F2A62B' },
        { id: 'endGrouping', text: '結束分組', color: '#E3563E', path:'/group_finish' },
    ];

    return(
        <div className="main">
        <Sidebar />
        <div className="main_container">
            <div className="data_header">
                <section className='page-back'>
                    <Link to="/group" className='page-back'>
                        <img src={Back} alt="" />
                    </Link>
                    <p className="font32" >自由分組</p>
                </section>
                <div className='page-time'>
                        <p className='font16_medium'>倒數 {remainingTime} 秒</p>
                    </div>
            </div>
            <GroupContext title="尚未有組別分組完成...請稍候" buttons={buttonsConfig} />
        </div>
    </div>
    )
}

export default Group2;
