import React from 'react';
import GroupPublic3 from './GroupPublic3';

const GroupContext3 = ({ title, buttons, dataList, activeComponent, onButtonClick }) => {
    const renderGroupPublics = () => {
        if (Array.isArray(dataList)) {
            return dataList.map((dataItem, index) => (
                <GroupPublic3
                    key={dataItem.id}
                    memberId={dataItem.id}
                    memberNames={dataItem.names}
                    showButton={true}
                    onButtonClick={() => onButtonClick(dataItem.id)}
                    isActive={activeComponent === dataItem.id && index === 0}
                />
            ));
        } else {
            return null;
        }
    };

    return (
        <div className='group_frame'>
            <div className='group_view'>
                <p className='group_view-title font24'>{title}</p>
                <section className='group_view-context2'>
                    {renderGroupPublics()}
                </section>
            </div>
            <div className='group_btn'>
                {buttons.map((button, index) => (
                    <button
                        key={button.id}
                        style={{
                            backgroundColor: button.color,
                            marginRight: button.id === activeComponent ? '20px' : '40px'
                        }}
                        onClick={() => {
                            onButtonClick(button.id);
                            button.onClick();
                        }}
                        className={button.id === activeComponent ? 'active' : ''}
                    >
                        {button.text}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default GroupContext3;
